using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.UI;public class Message : MonoBehaviour{    public float fadeDelay = 20; //seconds    public float fadeSpeed = .01f;    public Text msgText;    string username = "";    float timeCreated; //time displayed on hololens UI.    int serverTimestamp; //timestamp given in message json (seconds since server init)    // Use this for initialization    void Start ()    {        timeCreated = Time.time;        msgText.supportRichText = true;
    }		// Update is called once per frame	void Update ()    {                msgFader();    }    public void showMessage(string user = "", string msg = "")    {        Debug.Log(msgText.supportRichText.ToString());        username = "<b>" + user + "</b>";        Debug.Log(username);        msgText.text = username + ": " + msg;            }    //destroys gameobject after set amount of time    public void msgFader()    {        if (Time.time - timeCreated > fadeDelay && msgText.color.a > 0)        {            Color c = msgText.color;            c.a -= fadeSpeed;            msgText.color = c;            if (msgText.color.a < 0)            {                c.a = 0;                msgText.color = c;            }            if (msgText.color.a == 0)            {                Destroy(gameObject);            }        }    }}